using NUnit.Framework;
using System.Collections;
using System.Collections.Generic;
using UnityEngine;

namespace TSKT.TiledResolvers.Test
{
    public class Layer
    {
        [Test]
        [TestCase(
            "",
            "H4sIAAAAAAAACmNgGAWjITAaAsSEwDpGiCoYTYweeqsBuQ2G6W03IfsGc7jB3E4PN+6GpiNC4YUsvwcar6TooUQtyD50ABPDJkeJXaN6R0OA3BAAAISWtozACAAA",
            "eF5jYBgFoyEwGgLEhMA6RogqGE2MHnqrAbkNhultNyH7BnO4wdxODzfuhqYjQuGFLL8HGq+k6KFELcg+dAATwyZHiV2jekdDgNwQAADGNAkp")]
        public void DecompressData(string base64, string gzip, string zlib)
        {
            var bytes = System.Convert.FromBase64String(base64);
            var decompressedGzip = TiledResolvers.TileLayer.Data.DecompressGzip(System.Convert.FromBase64String(gzip));
            var decompressedZlib = TiledResolvers.TileLayer.Data.DecompressZlib(System.Convert.FromBase64String(zlib));

            Assert.AreEqual(bytes, decompressedGzip);
            Assert.AreEqual(bytes, decompressedZlib);
        }
    }
}
